'use strict'

var Trace = require('./lib/instrumentation/trace')
var http = require('http')

var buff = new Buffer('Hello World')

http.createServer(function (req, res) {
  var trace = new Trace({})
  trace.type = 'foo'
  trace.signature = 'bar'
  trace._recordStackTrace()
  res.end(buff)
}).listen(3000)
